import React, { useState, useRef } from "react";
import Webcam from "react-webcam";
import "./App.css";

function App() {
  const [mood, setMood] = useState(null);
  const [messages, setMessages] = useState([]); // chat messages
  const [input, setInput] = useState("");
  const webcamRef = useRef(null);

  const moodQuestions = {
    happy: "You look happy! What made you smile today? ðŸ˜Š",
    sad: "You seem a bit low. Want to share what's on your mind? ðŸ’™",
    angry: "You look frustrated. What happened? ðŸ˜¤",
    surprised: "You look surprised! Was it a good surprise? ðŸ˜®",
    neutral: "How are you feeling inside right now? ðŸ˜¶"
  };

  // Temporary mood detection (random)
  const detectMood = () => {
    const moods = ["happy", "sad", "angry", "surprised", "neutral"];
    const randomMood = moods[Math.floor(Math.random() * moods.length)];

    setMood(randomMood);

    setMessages([
      { sender: "system", text: `Mood detected: ${randomMood.toUpperCase()}` },
      { sender: "bot", text: moodQuestions[randomMood] }
    ]);
  };

  // Handle sending user message
  const sendMessage = () => {
    if (!input.trim()) return;

    const newMessages = [
      ...messages,
      { sender: "user", text: input }
    ];

    // Add a friendly bot response
    const botReply = getBotReply(mood, input);

    newMessages.push({ sender: "bot", text: botReply });

    setMessages(newMessages);
    setInput("");
  };

  // Safe non-therapist replies
  const getBotReply = (mood, text) => {
    if (mood === "happy") {
      return "That's really nice to hear! Moments like these make life feel brighter ðŸ’›";
    }
    if (mood === "sad") {
      return "Thank you for opening up. It's okay to feel this way â€” you're doing your best ðŸ’™";
    }
    if (mood === "angry") {
      return "That sounds frustrating. It's good that you're acknowledging your feelings ðŸ”¥";
    }
    if (mood === "surprised") {
      return "Wow, that does sound surprising! How are you feeling about it now? ðŸ¤”";
    }
    return "Thanks for sharing. It helps to express how you're feeling ðŸŒ¿";
  };

  return (
    <div className="container">
      <h1 className="title">Mood Buddy</h1>
      <p className="subtitle">Your friendly emotional check-in buddy ðŸ’›</p>

      <div className="layout">

        {/* Camera Panel */}
        <div className="left-panel">
          <h2>Your Camera</h2>

          <div className="webcam-box">
            <Webcam
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              videoConstraints={{ facingMode: "user" }}
            />
          </div>

          <button className="detect-btn" onClick={detectMood}>Detect Mood</button>
        </div>

        {/* Chat Panel */}
        <div className="right-panel">
          <h2>Conversation</h2>

          <div className="chat-window">
            {messages.map((msg, i) => (
              <div
                key={i}
                className={`bubble ${msg.sender}`}
              >
                {msg.text}
              </div>
            ))}
          </div>

          <div className="input-row">
            <input
              type="text"
              placeholder="Type your message..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
            />
            <button className="send-btn" onClick={sendMessage}>Send</button>
          </div>

        </div>
      </div>
    </div>
  );
}

export default App;
